    /**
     * Checks if the given input is a valid mobile number.
     * @param input to check.
     * @param minLength which it has to have.
     * @param maxLength which it can have.
     * @return true of false depending if it's valid or not.
     */
    public boolean checkTel(String input, int minLength, int maxLength) {
        Pattern p = Pattern.compile("^[\\\\+0-9\\\\-]*$");
        Matcher m = p.matcher(input.trim());
        valid = (input.trim().length() >= minLength && input.trim().length() <= maxLength && m.matches());
        return valid;
    }
    /**
     * Checks if the given input is a valid website link.
     * @param input to check.
     * @param minLength which it has to have.
     * @param maxLength which it can have.
     * @return true of false depending if it's valid or not.
     */
    public boolean checkWebpage(String input, int minLength, int maxLength){
        Pattern p = Pattern.compile("\\b[-a-zA-Z0-9+&@#/%?=~_|!:,.;]*[-a-zA-Z0-9+&@#/%=~_|]");
        Matcher m = p.matcher(input.trim());
        valid = (input.trim().length()>=minLength && input.trim().length()<=maxLength && m.matches());
        return valid;
    }

    public boolean checkNumber(String input){
        Pattern p = Pattern.compile("^[0-9]+$");
        Matcher m = p.matcher(input.trim());
        valid = m.matches();
        return valid;
    }
